import React from 'react';
import Portfolio from './component/Portfolio';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import UsernamePage from './component/UserNamePage'; 
import PortfolioForm from './component/PortfolioForm'; 


function App() {

  return (
    <Router>
      <div>
        <Routes>
          {/* Route for entering the username */}
          <Route path="/" element={<UsernamePage />} />

          {/* Route for displaying the portfolio with the given username */}
          <Route path="/api/portfolio/:username" element={<Portfolio />} />
        </Routes>
        <Routes>
          <Route exact path="/" component={HomePage} /> {/* Home page or main landing page */}
          <Route path="/portfolio-form" component={PortfolioForm} />
        </Routes>
      </div>
    </Router>

  );
}
export default App;
