// Portfolio.js
import React, { useEffect, useState } from 'react';

function Portfolio({ username }) {
    //return <div>Displaying portfolio for user: {username}</div>;

  const [portfolio, setPortfolio] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    <div>Displaying portfolio for user: {username}</div>;

    fetch(`http://localhost:8081/api/portfolio/${username}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`Error fetching portfolio for username: ${username}`);
        }
        return response.json();
      })
      .then((data) => {
        setPortfolio(data);
        setLoading(false);
      })
      .catch((error) => {
        setError(error.message);
        setLoading(false);
      });
  }, [username]);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }

  if (!portfolio) {
    return <p>No portfolio found for username: {username}</p>;
  }

  return (
    <div>
      <h1>{portfolio.fullName}'s Portfolio</h1>
      <p>Username: {portfolio.username}</p>
      <p>Email: {portfolio.email}</p>
      <p>Role: {portfolio.role}</p>
      <p>About Me: {portfolio.about_me}</p>
      <p>Mobile: {portfolio.mobile}</p>
    </div>
  );
}

export default Portfolio;
