// Portfolio.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function Portfolio() {
  const { username } = useParams();  // Get the username from the URL parameter
  const [portfolio, setPortfolio] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(`https://api.example.com/api/portfolio/${username}`)  // Replace with your API endpoint
      .then((response) => response.json())
      .then((data) => {
        setPortfolio(data);
        setLoading(false);
      })
      .catch((error) => {
        setError('Failed to fetch data');
        setLoading(false);
      });
  }, [username]);  // Re-fetch when username changes

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h1>{portfolio ? `${portfolio.fullName}'s Portfolio` : 'No Portfolio Found'}</h1>
      <p><strong>Username:</strong> {username}</p>
      <h3>Projects</h3>
      <ul>
        {portfolio && portfolio.projects && portfolio.projects.length > 0 ? (
          portfolio.projects.map((project) => (
            <li key={project.id}>
              <h4>{project.name}</h4>
              <p>{project.description}</p>
            </li>
          ))
        ) : (
          <p>No projects available.</p>
        )}
      </ul>
    </div>
  );
}

export default Portfolio;
