// Portfolio.js
import React, { useEffect, useState } from 'react';

function Portfolio({ username }) {
    //return <div>Displaying portfolio for user: {username}</div>;

  const [portfolio, setPortfolio] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {


    fetch(`http://localhost:8081/api/portfolio/${username}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`Error fetching portfolio for username: ${username}`);
        }
        return response.json();
      })
      .then((data) => {
        setPortfolio(data);
        setLoading(false);
      })
      .catch((error) => {
        setError(error.message);
        setLoading(false);
      });
  }, [username]);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }

  if (!portfolio) {
    return <p>No portfolio found for username: {username}</p>;
  }

  return (
    <div>
      <h1>{portfolio.fullName}'s Portfolio</h1>
      <p>Username: {portfolio.username}</p>
      <p>Email: {portfolio.email}</p>
      <p>Role: {portfolio.role}</p>
      <p>About Me: {portfolio.about_me}</p>
      <p>Mobile: {portfolio.mobile}</p>
      <h3>Skills</h3>
      <ul>
        {portfolio.skill_list && portfolio.skill_list.length > 0 ? (
          portfolio.skill_list.map((skill_list) => (
            <li key={skill_list.id}>
              <strong>{skill_list.skillName}</strong> - {skill_list.proficiency}
            </li>
          ))
        ) : (
          <p>No skills listed.</p>
        )}
      </ul>
      <h3>Projects</h3>
      <ul>
        {portfolio.project_list && portfolio.project_list.length > 0 ? (
          portfolio.project_list.map((project_list) => (
            <li key={project_list.id}>
              <strong>{project_list.projectName}</strong><br />{project_list.description}<br />URL-{project_list.url}
            </li>
          ))
        ) : (
          <p>No skills listed.</p>
        )}
      </ul>
    </div>
  );
}

export default Portfolio;
